import React from "react";
import { Player, ControlBar } from "video-react";
import "./Watch.css";
import Scene from "./Scene";

export default class Watch extends React.Component {
  constructor() {
    super();
    this.handleVideoLoad = this.handleVideoLoad.bind(this);
    this.handleOnSelect = this.handleOnSelect.bind(this);
    this.state = {
      videoFileURL: "",
      videoFileObject: "",
      videoName: "",
      renderQueryData: false,
      seriesOptions: [{ series_name: "Select Series" }],
      episodeOptions: [{ episode_name: "Select Episode" }]
    };
  }
  componentDidMount() {
    this.queryForSeries();
  }
  handleVideoLoad(e) {
    let files = e.target.files;
    if (files.length === 1) {
      let file = files[0];
      console.log(file.name.substring(0, file.name.length - 4));
      this.setState({
        videoFileURL: URL.createObjectURL(file),
        videoFileObject: file,
        videoName: file.name
      });
    }
  }
  handleOnSelect(e) {
    const data = this.state.seriesOptions;
    for (var elem in data) {
      console.log(elem);
      console.log(e.target.value);
      if (elem.series_name == e.target.value) {
        console.log(e.target.value);
        console.log("hello");
        this.queryForEpisodes(elem.series_id);
        break;
      }
    }
  }
  queryForSeries() {
    fetch("https://scene-stamp-server.herokuapp.com/getSeriesData")
      .then(response => response.json())
      .then(data => this.setState({ seriesOptions: data }))
      .then(data => this.prependAppendSeries());
  }
  queryForEpisodes(id) {
    fetch(
      "https://scene-stamp-server.herokuapp.com/getEpisodeDataFromSeries?series_ids=" +
        id
    )
      .then(response => response.json())
      .then(data => this.setState({ episodeOptions: data }))
      .then(this.prependAppendEpisodes());
  }

  // 50519
  prependAppendEpisodes() {
    let temp = this.state.episodeOptions;
    temp.unshift({ episode_name: "Create New Episode" });
    temp.unshift({ episode_name: "Select Episode" });
    this.setState({ episodeOptions: temp });
  }

  prependAppendSeries() {
    let temp = this.state.seriesOptions;
    temp.unshift({ series_name: "Create New Series" });
    temp.unshift({ series_name: "Select Series" });
    this.setState({ seriesOptions: temp });
  }
  render() {
    return (
      <div className="watch-container">
        <div className="watch-elements-container">
          <select onChange={this.handleOnSelect}>
            {this.state.seriesOptions.map(element => (
              <option className="watch-element">{element.series_name}</option>
            ))}
          </select>
          <select>
            {this.state.episodeOptions.map(element => (
              <option className="watch-element">{element.episode_name}</option>
            ))}
          </select>
        </div>
        <div className="video">
          <Player ref="player" src={this.state.videoFileURL} />
        </div>
        <div className="watch-scenes">
          <Scene episode="Episode 5 Season 1" />
          <Scene episode="Epside 1 Season 3" />
        </div>
      </div>
    );
  }
}
