import React from "react";
import { Player, ControlBar } from "video-react";
import "./Watch.css";
import Scene from "./Scene";

export default class Watch extends React.Component {
  constructor() {
    super();
    this.handleVideoLoad = this.handleVideoLoad.bind(this);
    this.state = {
      videoFileURL: "",
      videoFileObject: "",
      videoName: "",
      renderQueryData: false,
      seriesOptions: ["Select Series", "+ Create New Series"],
      episodeOptions: ["Select Episode", "Create New Episode"]
    };
  }
  componentDidMount() {
    console.log("test");
    this.queryForSeries();
  }
  handleVideoLoad(e) {
    let files = e.target.files;
    if (files.length === 1) {
      let file = files[0];
      console.log(file.name.substring(0, file.name.length - 4));
      this.setState({
        videoFileURL: URL.createObjectURL(file),
        videoFileObject: file,
        videoName: file.name
      });
    }
  }
  queryForSeries() {
    fetch("https://scene-stamp-server.herokuapp.com/getSeriesData")
      .then(response => response.json())
      .then(data => this.setState({ seriesOptions: data }))
      .then(this.prependAppendData("Series"));
  }
  queryForEpisodes(id) {
    fetch(
      "https://scene-stamp-server.herokuapp.com/getEpisodeDataFromSeries?series_ids=" +
        id
    )
      .then(response => response.json())
      .then(data => this.setState({ episodeOptions: data }))
      .then(this.prependAppendData("Episode"));
  }
  prependAppendData(text) {
    debugger;
    const data = [
      { series_name: "Select " + text },
      { series_name: "Create New " + text }
    ];
    let temp = this.state.seriesOptions;
    temp.unshift(data);
    this.setState({ seriesOptions: temp });
  }
  render() {
    return (
      <div className="watch-container">
        <div className="watch-elements-container">
          <select>
            {this.state.seriesOptions.map(element => (
              <option className="watch-element">{element.series_name}</option>
            ))}
          </select>
          <select>
            {this.state.seriesOptions.map(element => (
              <option className="watch-element">{element.episode_data}</option>
            ))}
          </select>
        </div>
        <div className="video">
          <Player ref="player" src={this.state.videoFileURL} />
        </div>
        <div className="watch-scenes">
          <Scene episode="Episode 5 Season 1" />
          <Scene episode="Epside 1 Season 3" />
        </div>
      </div>
    );
  }
}
